import React from 'react';
import "./Counter.css";

class Counter extends React.Component {
     value = this.myRef = React.createRef();
    countOnCLick = ()=>{
        value.current.textContent = 2;
    }
     curr = value.current.textContent;
  
  
  
    render = () => {    
    return (     
        <div className="counter">
            <span ref={value}>1</span>
            <input type="text" name="" id=""/>
            <button onClick={countOnCLick}>click</button>
        </div>
    )
  }
}

export default Counter;
